mapper.update:
update sys_dict
set code = #{code,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, code, name, type
from sys_dict
where id = #{id,jdbcType=VARCHAR}

select id, code, name, type
from sys_dict
where type = #{type,jdbcType=VARCHAR}
